"use strict";

$(window).on('action:ajaxify.end', function(event, data){
	console.log("here")
	if(data.tpl_url === 'home'){
		$('#search-input').keydown(doSearch);
	}
})

function doSearch(event){
	if(event.keyCode==13){
		var query = $('#search-input').val();
		$('#search-form').find('input').val(query);
		$('#search-form').submit();
	}
}
