"use strict";

$(window).on('action:ajaxify.end', function(event, data){
	if(data.url === ""){
		var searchInput = $('#search-input');
		searchInput.focus();
		searchInput.keydown(doSearch);
	}
})

function doSearch(event){
	if(event.keyCode==13){
		var query = $('#search-input').val();
		$('#search-form').find('input').val(query);
		$('#search-form').submit();
	}
}
